  return collection.sort((value1, value2) => {
    const comparable1 = iteratee(value1);
    const comparable2 = iteratee(value2);
    if (comparable1 < comparable2) return -1;
    if (comparable1 > comparable2) return 1;
    return 0;
  });
}
