function mapKeys(obj, fn) {
  const newObj = {};
  for (const [key, value] of Object.entries(obj)) {
    newObj[fn(key)] = value;
  }
  return newObj;
}

function mapValues(obj, fn) {
  const newObj = {};
  for (const [key, value] of Object.entries(obj)) {
    newObj[key] = fn(value);
  }
  return newObj;
}

function invert(obj) {
  const newObj = {};
  for (const [key, value] of Object.entries(obj)) {
    newObj[value] = key;
  }
  return newObj;
}

function intersection(array1, array2) {
  return array1.filter(x => array2.includes(x));
}

function* zip(...arrays) {
  if (arrays.length === 0) return;
  const minLength = Math.min(...arrays.map(arr => arr.length));
  for (let i = 0; i < minLength; ++i) {
    yield arrays.map(arr => arr[i]);
  }
}

function truncate(str, maxSize, { truncIndicator = 'â€¦' } = {}) {
  if (str.length <= maxSize) return str;
  return str.slice(0, maxSize) + truncIndicator;
}

function trim(str, toRemove = ' \t\n') {
  const chars = str.split('');
  while (chars.length && toRemove.includes(chars[0])) chars.shift();
  while (chars.length && toRemove.includes(chars.at(-1))) chars.pop();
  return chars.join('');
}

function trimLeft(str, toRemove = ' \t\n') {
  const leftBound = str.split('').findIndex(c => !toRemove.includes(c));
  return leftBound === -1 ? '' : str.slice(leftBound);
}

function trimRight(str, toRemove = ' \t\n') {
  const chars = str.split('');
  while (chars.length && toRemove.includes(chars[chars.length - 1])) {
    chars.pop();
  }
  return chars.join('');
}

function randomInt(min, max) {
  return min + Math.floor(Math.random() * (max - min));
}

function shuffle(array) {
  return array
    .map(value => ({ value, sortKey: Math.random() }))
    .sort((a, b) => a.sortKey - b.sortKey)
    .map(({ value }) => value);
}

function el(tagName, attrs = {}, children = []) {
  const newElement = document.createElement(tagName);
  for (const [key, value] of Object.entries(attrs)) {
    newElement.setAttribute(key, value);
  }
  newElement.append(...children);
  return newElement;
}

function fragment(children = []) {
  const newFragment = new DocumentFragment();
  newFragment.append(...children);
  return newFragment;
}

function memoize(fn, { argsToKey = null } = {}) {
  argsToKey =
    argsToKey ??
    ((...args) => {
      if (args.length !== 1) {
        throw new Error(
          'Exactly one argument must be passed in to this memoized function ' +
            '(unless a custom args-to-key mapper is provided)'
        );
      }
      return args[0];
    });

  const cache = new Map();
  return (...args) => {
    const key = argsToKey(...args);
    if (cache.has(key)) return cache.get(key);

    const res = fn(...args);
    cache.set(key, res);
    return res;
  };
}

function createEventEmitter() {
  const listeners = [];
  return {
    subscribe(fn) {
      listeners.push(fn);
      let unsubscribed = false;
      return function unsubscribe() {
        if (unsubscribed) return;
        const index = listeners.indexOf(fn);
        listeners.splice(index, 1);
        unsubscribed = true;
      };
    },
    trigger(...args) {
      return listeners.map(fn => fn(...args));
    },
  };
}

function addToDate(date_, offset) {
  const {
    years = 0,
    months = 0,
    days = 0,
    hours = 0,
    minutes = 0,
    seconds = 0,
    ms = 0,
  } = offset;

  const date = new Date(date_.valueOf());
  date.setFullYear(date.getFullYear() + years);
  date.setMonth(date.getMonth() + months);
  date.setDate(date.getDate() + days);
  date.setHours(date.getHours() + hours);
  date.setMinutes(date.getMinutes() + minutes);
  date.setSeconds(date.getSeconds() + seconds);
  date.setMilliseconds(date.getMilliseconds() + ms);
  return date;
}
